package com.books.store_books.controller;import com.books.store_books.login.Login;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@CrossOrigin(origins = "http://localhost:4200")@RestController@RequestMapping("/api/auth")public class AuthController {    @PostMapping("/login")    public ResponseEntity<String> login(@RequestBody Login loginRequest) {        if ("user".equals(loginRequest.getUsername()) && "pass".equals(loginRequest.getPassword())) {            return ResponseEntity.ok("Login successful");        } else {            return ResponseEntity.status(401).body("Invalid credentials");        }    }}