package com.books.store_books.config;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.security.web.SecurityFilterChain;import org.springframework.security.config.annotation.web.builders.HttpSecurity;@Configurationpublic class SecurityConfig {    @Bean    public SecurityFilterChain securityFilterChain(HttpSecurity http) throws Exception {        http                .authorizeHttpRequests((authorize) -> authorize                        .requestMatchers("/api/auth/login").permitAll()                        .anyRequest().permitAll()                )                .csrf((csrf) -> csrf.disable());        return http.build();    }}