package com.books.store_books.user;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.crypto.password.PasswordEncoder;import org.springframework.stereotype.Service;@Servicepublic class AppUserService {    @Autowired    private AppUserRepository userRepository;    @Autowired    private PasswordEncoder passwordEncoder;    public AppUser registerUser(String username, String password) {        AppUser user = new AppUser();        user.setUsername(username);        user.setPassword(passwordEncoder.encode(password));        user.setRole("USER");        return userRepository.save(user);    }}